def postCodeSorter(day, zones, array):
    for postcode in array:
        found = False
        for i in range(len(zones)):
            if (zones[i][0] == postcode):
                zones[i][1].append(day)
                found = True
        if (found == False):
            zones.append([postcode, [day]])
    return zones        



